import React, {
  useLayoutEffect,
  useCallback,
  useReducer,
  useState,
} from 'react';

import {
  ScrollView,
  Text,
  Image,
  StyleSheet,
  View,
  Button,
} from 'react-native';
import HeadButton from '../../Components/HeaderButton';
import { useSelector, useDispatch } from 'react-redux';
import HeaderButton from '../../Components/HeadButton';
import Input from '../../Components/UI/Input';
import Card from '../../Components/UI/Card';
import { logout } from '../../store/actions/auth';
const FORM_INPUT_UPDATE = 'FORM_INPUT_UPDATE';

const formReducer = (state, action) => {
  if (action.type === FORM_INPUT_UPDATE) {
    const updatedValues = {
      ...state.inputValues,
      [action.input]: action.value,
    };
    const updatedValidities = {
      ...state.inputValidities,
      [action.input]: action.isValid,
    };
    let updatedFormIsValid = true;
    for (const key in updatedValidities) {
      updatedFormIsValid = updatedFormIsValid && updatedValidities[key];
    }
    return {
      formIsValid: updatedFormIsValid,
      inputValidities: updatedValidities,
      inputValues: updatedValues,
    };
  }
  return state;
};
const UserScreen = (props) => {
  const user = useSelector((state) => state.auth.user);
  let name,
    mobile,
    email,
    photo =
      'data:image/png;base64,';
  if (user) {
    name = user.name.toUpperCase();
    mobile = user.mobile ? user.mobile.toString() : '';
    email = user.email;
    if (user.photo) photo = user.photo;
  }
  const [isLoading, setIsLoading] = useState(false);
  const dispatch = useDispatch();
  useLayoutEffect(() => {
    props.navigation.setOptions({
      headerLeft: () => {
        return (
          <HeaderButton
            name="ios-menu"
            color="white"
            size={25}
            onPress={() => {
              props.navigation.toggleDrawer();
            }}
          />
        );
      },
      headerRight: () => {
        return (
          <HeadButton
            name="Logout"
            color="#a100f2"
            onPress={() => {
              props.navigation.replace('Signin');
              dispatch(logout());
            }}
          />
        );
      },
    });
  });
  const [formState, dispatchFormState] = useReducer(formReducer, {
    inputValues: {
      email: '',
      password: '',
    },
    inputValidities: {
      email: false,
      password: false,
    },
    formIsValid: false,
  });
  const inputChangeHandler = useCallback(
    (inputIdentifier, inputValue, inputValidity) => {
      dispatchFormState({
        type: FORM_INPUT_UPDATE,
        value: inputValue,
        isValid: inputValidity,
        input: inputIdentifier,
      });
    },
    [dispatchFormState]
  );

  return (
    // <KeyboardAvoidingView style={{ flex: 1 }}>
    <ScrollView contentConstytainerStyle={{ flex: 1 }}>
      <View style={styles.head}>
        <Image
          style={styles.image}
          source={{
            uri: photo,
          }}
        />
        <Text style={{ color: 'white', fontSize: 15, paddingVertical: 10 }}>
          {name}
        </Text>
      </View>
      <Text
        style={{
          color: 'black',
          fontWeight: 'bold',
          fontSize: 20,
          paddingVertical: 10,
          paddingLeft: 10,
        }}
      >
        Personal Information
      </Text>

      <Card style={styles.authContainer}>
        <ScrollView>
          <Input
            id="email"
            label="E-Mail"
            keyboardType="email-address"
            required
            email
            autoCapitalize="none"
            errorText="Please enter a valid email address."
            onInputChange={inputChangeHandler}
            initialValue={email}
          />

          <Input
            id="Name"
            label="Name"
            keyboardType="default"
            required
            autoCapitalize="none"
            errorText="Please enter a valid name."
            onInputChange={inputChangeHandler}
            initialValue={name}
          />
          <Input
            id="number"
            label="Mobile"
            keyboardType="phone-pad"
            required
            minLength={5}
            autoCapitalize="none"
            errorText="Please enter a valid number."
            onInputChange={inputChangeHandler}
            initialValue={mobile}
          />

          <View style={styles.buttonContainer}>
            {isLoading ? (
              <ActivityIndicator size="small" color={'#a100f2'} />
            ) : (
              <Button title="Update" color={'#a100f2'} onPress={() => {}} />
            )}
          </View>
          {/* <View style={styles.buttonContainer}>
              <Button
                title={`Switch to ${isSignup ? "Login" : "Sign Up"}`}
                color={"green"}
                onPress={() => {
                  setIsSignup((prevState) => !prevState);
                }}
              />
            </View> */}
        </ScrollView>
      </Card>
    </ScrollView>
    // </KeyboardAvoidingView>
  );
};

export default UserScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  head: {
    width: '100%',
    minHeight: 220,
    height: '24%',
    backgroundColor: '#6a00f4',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    width: 150,
    height: 150,
    borderRadius: 75,
  },
  authContainer: {
    width: '100%',
    minHeight: '100%',
  },
});
